import React, {useState} from 'react';
import { Link } from 'react-router-dom';
import {Formik} from 'formik'
import './RegisterPage.css';
import axios from 'axios';
import '../../App.css';

export default function SignUpPage() {
  const [data, setData] = useState({
    username:"",
    email:"",
    password:""
})
  function handle(e){
    const newData = {...data}
    newData[e.target.id] = e.target.value
    setData(newData)
    console.log(newData)
  }
  const handleSubmit = (event) => {
    console.log(data);
    axios({
      url: "http://localhost:5000/register",
      method: "POST",
      body: JSON.stringify(data)
    })
      .then((response) => {
        console.log("Data has been retreived the server");
        return response.json();
      })
      .catch(() => {
        console.log("Internal server error");
      });
      event.preventDefault()
  };
  return (
    <div className="text-center m-5-auto parent2">
      <h2 className="fs-3">Join us</h2>
      <h4 className="fs-5">Create your personal account</h4>
      <Formik initialValues={{username:"",email:"",password:""}} onSubmit={data=>{
        console.log(data)
      }}>
        {({values, handleChange, handleBlur, handleSubmit})=>(
        <form onSubmit={handleSubmit}>
          <p>
            <label className="fs-3">Username</label>
            <br />
            <input type="text" name="username" onChange={(e)=>{s+=e.target.value}} required />
          </p>
          <p>
            <label className="fs-3">Email address</label>
            <br />
            <input type="email" name="email" onChange={(e)=>handle(e)} value={data.email} required />
          </p>
          <p>
            <label className="fs-3">Password</label>
            <br />
            <input type="password" name="password" onChange={(e)=>handle(e)} value={data.password} requiredc />
          </p>
          <p>
            <input type="checkbox" name="checkbox" id="checkbox" required />{' '}
            <span className="fs-5">
              I agree all statements in{' '}
              <a
                href="https://google.com"
                target="_blank"
                rel="noopener noreferrer"
              >
                terms of service
              </a>
            </span>
            .
          </p>
          <p>
            <button id="sub_btn" type="submit" onClick={handleSubmit}>
              Register
            </button>
          </p>
        </form>
        )}
      </Formik>
      <footer>
        <p>
          <Link to="/">Back to Homepage</Link>.
        </p>
      </footer>
    </div>
  );
}
